EOS
  class << self
    def calc_p1
      left = INPUT.chars.count("(")
      right = INPUT.chars.count(")")
      left - right
    end

    def calc_p2
      floor = 0
      INPUT.chars.each_with_index do |c, idx|
        case c
        when "(" then floor += 1
        when ")" then floor -= 1
        else
          raise "unexpected char"
        end
        return idx + 1 if floor == -1
      end
    end
  end
end
puts Day1.calc_p1
puts Day1.calc_p2
